CREATE TABLE tickets (id UUID DEFAULT gen_random_uuid() PRIMARY KEY,title TEXT NOT NULL,description TEXT NOT NULL,status TEXT NOT NULL CHECK (status IN ('open', 'in_progress', 'resolved', 'closed')),priority TEXT NOT NULL CHECK (priority IN ('low', 'medium', 'high', 'urgent')),created_at TIMESTAMPTZ DEFAULT NOW(),updated_at TIMESTAMPTZ DEFAULT NOW(),created_by TEXT,assigned_to TEXT,tags TEXT[] DEFAULT '{}',internal_notes TEXT);
